import React, { Component } from 'react';
import {
    Modal, ModalHeader, ModalBody, Label, Row, Col
} from 'reactstrap';
import { Control, LocalForm, Errors } from 'react-redux-form';

//Functions for Validations
//(1)Required field
const required = (val) => val && val.length;
//(2)Range 
const maxLength = (len) => (val) => !(val) || (val.length <= len);
const minLength = (len) => (val) => !(val) || (val.length >= len);

export class CommentForm extends Component {

    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(values) {
        console.log('Current State is: ' + JSON.stringify(values));
        alert('Current State is: ' + JSON.stringify(values));
    };

    render() {
        return (
            <Modal isOpen={this.props.show} toggle={this.props.handleClose}>
                <ModalHeader>Submit Comment</ModalHeader>
                <ModalBody>
                    <LocalForm onSubmit={(values) => this.handleSubmit(values)}>
                        <Row className="form-group">
                            <Label htmlFor="firstname">First Name</Label>
                            <Control.select model=".rating" name="rating" id="rating">
                                <option>1</option>
                                <option>2</option>
                                <option>3</option>
                                <option>4</option>
                                <option>5</option>
                            </Control.select>
                            <Control.text model=".firstname" id="firstname" name="firstname" placeholder="FirstName" className="form-control"/>
                        </Row>
                    </LocalForm>
                </ModalBody>
            </Modal>
        );
    }
}
