import * as ActionTypes from './ActionTypes';

//Action addComment which will be used to take form input and add that data to existing data.
export const addComment = (dishId, rating, author, comment) => ({
    type : ActionTypes.ADD_COMMENT,
    payload : {
        dishId : dishId,
        rating : rating,
        author : author,
        comment : comment
    }
});
