import React, { Component } from 'react';
import { Card, CardImg, CardBody, CardTitle, CardText,BreadcrumbItem,Breadcrumb,Button ,
    Modal, ModalHeader, ModalBody, Label, Row} from 'reactstrap';
import {Link} from 'react-router-dom';
import 'font-awesome/css/font-awesome.css';
import { Control, LocalForm, Errors } from 'react-redux-form';

//Functions for Validations
//(1)Required field
const required = (val) => val && val.length;
//(2)Range 
const maxLength = (len) => (val) => !(val) || (val.length <= len);
const minLength = (len) => (val) => !(val) || (val.length >= len);

class CommentForm extends Component {

    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(values) {
        console.log('Current State is: ' + JSON.stringify(values));
        alert('Current State is: ' + JSON.stringify(values));
        this.props.handleClose();
    };

    render() {
        return (
            <Modal isOpen={this.props.show} toggle={this.props.handleClose}>
                <ModalHeader><b>Submit Comment</b></ModalHeader>
                <ModalBody>
                    <div className="container">
                        <LocalForm onSubmit={(values) => this.handleSubmit(values)}>
                            <Row className="form-group">
                                <Label htmlFor="rating"><b>Rating</b></Label>
                                <Control.select model=".rating" name="rating" id="rating" className="form-control" defaultValue="1">
                                    <option value="1">1</option>
                                    <option value="2">2</option>
                                    <option value="3">3</option>
                                    <option value="4">4</option>
                                    <option value="5">5</option>
                                </Control.select>
                            </Row>
                            <Row className="form-group">
                                <Label htmlFor="your name"><b>Your Name</b></Label>
                                <Control.text model=".yourname" id="lastname" name="lastname" placeholder="Your Name" className="form-control"
                                    validators={{
                                        required,minLength: minLength(3), maxLength: maxLength(15)
                                    }}/>
                                <Errors className="text-danger" model=".yourname" show="touched" messages={{
                                    required : ' Must be greater than 2 characters',
                                    minLength: ' Must be greater than 2 characters',
                                    maxLength: ' Must be 15 characters or less',
                                }} />
                            </Row>
                            <Row className="form-group">
                                    <Label htmlFor="comment"><b>Comment</b></Label>
                                    <Control.textarea model=".message" id="message" name="message" placeholder="" rows="6" className="form-control"/>        
                            </Row>
                            <Row className="form-group">
                                <Button type="submit" color="primary">
                                    Submit
                                </Button>
                            </Row>
                        </LocalForm>
                    </div>

                </ModalBody>
            </Modal>
        );
    }
}



class DishDetail extends Component {

    constructor(props){
        super(props);
        this.state={
            comments : this.props.comments,
            isCommentModalOpen : false
        }
        this.toggleCommentModal = this.toggleCommentModal.bind(this);
    }

    toggleCommentModal(){
        this.setState({isCommentModalOpen : !this.state.isCommentModalOpen});
    }

    getFormatedDate(date) {
        let d = new Date(date);
        var months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
        return (" , " + months[d.getMonth()] + " " + d.getDate() + ", " + d.getFullYear())
    }

    render() {
        const cmt = this.props.comments.map((c) => {
            return (
                <div key = {c.id}>
                    <ul className="list-unstyled">
                        <li>{c.comment}</li>
                        <li>-- {c.author}{this.getFormatedDate(c.date)}</li>
                    </ul>
                </div>
            )
        });

        return (
            <div className="container">
            <div className="row">
                <Breadcrumb>
                <BreadcrumbItem><Link to="/home">Home</Link></BreadcrumbItem>
                <BreadcrumbItem active>{this.props.dish.name}</BreadcrumbItem>
                </Breadcrumb>
            </div>
            <div className="row">
                <div className="col-12 col-md-5 mt-3 mb-3">
                    <Card>
                        <CardImg top width="100%" src={this.props.dish.image} alt="Card image cap" />
                        <CardBody>
                            <CardTitle tag="h5">{this.props.dish.name}</CardTitle>
                            <CardText>{this.props.dish.description}</CardText>
                        </CardBody>
                    </Card>
                </div>
                <div className="col-12 col-md-5 mt-3 mb-3">
                    <h3>Comments</h3>
                    {cmt}
                    <div className="row">
                <Button outline color="secondary" onClick={this.toggleCommentModal}><i class="fa fa-lg fa-pencil" aria-hidden="true"></i> Submit Comment</Button>{' '}
                </div>
                    <CommentForm show={this.state.isCommentModalOpen} handleClose={this.toggleCommentModal}/>
                </div>
            </div>
            </div>
        )
    }

}


export default DishDetail;
